import React, { useState } from 'react'
import './updateui.css'

const UpdateUi = () => {

    const [formData, setFormData] = useState({
        vendor: '',
        product: '',
        amount: '',
        date: ''
    });
    const { vendor, product, amount, date } = formData;

    const handleInput = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            // Perform the PUT request to update the invoice data
            const response = await axios.put(`http://localhost:8080/invoice/${invoiceId}`, formData);
            
            console.log('Invoice updated:', response.data); // Log the updated invoice data
            
            // After successful submission, you might want to clear the form fields
            setFormData({
                vendor: '',
                product: '',
                amount: '',
                date: ''
            });
            
            
        } catch (error) {
            console.error('Error updating invoice:', error);
            
        }
    };





  return (
    <div className='fcontainer'>
    <form class="form" onSubmit={handleSubmit}>
  <div class="form__title">Invoice</div>
  
  <div class="form__item">
    <label  class="form__label">Vendor </label>
    <input type="text" value={vendor} onChange={handleInput} class="form__input" name="vendor"  />
    
  </div>

  <div class="form__item">
    <label  class="form__label">Product </label>
    <input type="text" value={product} nChange={handleInput} class="form__input" name="product" />
    
  </div>
  <div class="form__item">
    <label  class="form__label">Amount </label>
    <input type="number" value={amount} onChange={handleInput} class="form__input" name="amount" />
    
  </div>
  
  <div class="form__item">
    <label  class="form__label">Date</label>
    <input type="date" value={date} onChange={handleInput} class="form__input form__input--small" name="date"  />
   
  </div>
  
  <div class="form__item">
    <button class="form__btn" type="submit">Add</button>
  </div>
</form>
</div>
  )
}

export default UpdateUi
