import React, { useEffect, useState } from 'react'
import './addui.css'
import axios from 'axios'
import {  useNavigate } from 'react-router-dom';

const AddUi = () => {
    const api="http://localhost:8080/invoice";
    const navigate=useNavigate();
    const [post,setPost]=useState({
        vendor: '',
        amount: '',
        product: '',
        date: '',
    });
    const handleInput = (e) => {
        setPost({...post,[e.target.name]: e.target.value})
    }

    function handleSubmit(e){
      e.preventDefault();
      
      axios.post(api)
      .then(resp => console.log(resp))
      .catch(err => console.log(err));

      navigate('/invoice');
    }


  return (
    <form class="form-style-4" action="" method="post">
<label for="field1">
<span>Enter Your Name</span><input type="text" name="field1" required="true" />
</label>
<label for="field2">
<span>Email Address</span><input type="email" name="field2" required="true" />
</label>
<label for="field3">
<span>Short Subject</span><input type="text" name="field3" required="true" />
</label>
<label for="field4">
<span>Message to Us</span><textarea name="field4" onkeyup="adjust_textarea(this)" required="true"></textarea>
</label>
<label>
<span> </span><input type="submit" value="Send Letter" />
</label>
</form>
  )
}

export default AddUi
