import React, { useEffect, useState } from 'react'
import './addui.css'
import axios from 'axios'
import {  useNavigate } from 'react-router-dom';

const AddUi = () => {
    const api="http://localhost:8080/invoice";
    const navigate=useNavigate();
    const [post,setPost]=useState({
        vendor: '',
        amount: '',
        product: '',
        date: '',
    });
    const handleInput = (e) => {
        setPost({...post,[e.target.name]: e.target.value})
    }

    function handleSubmit(e){
      e.preventDefault();
      
      axios.post(api)
      .then(resp => console.log(resp))
      .catch(err => console.log(err));

      navigate('/invoice');
    }


  return (
    <form class="form" action="./" method="POST">
  <div class="form__title">Invoice</div>
  
  <div class="form__item">
    <label  class="form__label">Vendor </label>
    <input type="text" class="form__input" name="vendor"  placeholder="Vendor name"/>
    
  </div>

  <div class="form__item">
    <label  class="form__label">Amount </label>
    <input type="number" class="form__input" name="amount"  placeholder="Vendor name"/>
    
  </div>
  
  <div class="form__item">
    <label  class="form__label">Date</label>
    <input type="date" class="form__input form__input--small" name="date"  placeholder="Date"/>
   
  </div>
  <div class="form__item">
    
    
  </div>
  <div class="form__item">
    <button class="form__btn" type="submit">Add</button>
  </div>
</form>
  )
}

export default AddUi
